import React, { useState } from 'react';
import { View, Text, TextInput, TouchableOpacity, Image, StyleSheet } from 'react-native';
import ImagePicker from 'react-native-image-crop-picker';
import Icon from 'react-native-vector-icons/Ionicons';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

const NewPostScreen = () => {
  const [itemName, setItemName] = useState('');
  const [itemLocation, setItemLocation] = useState('');
  const [itemCategory, setItemCategory] = useState('');
  const [selectedImage, setSelectedImage] = useState(null);

  const handleSelectImage = () => {
    ImagePicker.openPicker({
      width: 1080,
      height: 1080,
      cropping: true,
    }).then((image) => {
      console.log('Imagem selecionada: ', image.path);
      setSelectedImage(image);
    }).catch((error) => {
      console.log('Erro ao selecionar imagem: ', error);
    });
  };

  const handleSubmit = () => {
    // Implemente a lógica para enviar os dados do novo post para a API
    // Lembre-se de utilizar os estados itemName, itemLocation, itemCategory, etc.
  };

  return (
    <View style={styles.container}>
      {/* Exibição da imagem selecionada */}
      {selectedImage && (
        <Image source={{ uri: selectedImage.path }} style={styles.selectedImage} />
      )}

      {/* Botão para selecionar imagem */}
      <TouchableOpacity style={styles.selectImageButton} onPress={handleSelectImage}>
        <Icon name="camera-outline" size={30} color="#22c55e" />
        <Text style={styles.selectImageText}>Selecionar Imagem</Text>
      </TouchableOpacity>

      <TextInput
        style={styles.input}
        placeholder="Nome do Item"
        placeholderTextColor="#8a8a8f"
        value={itemName}
        onChangeText={setItemName}
      />
      <TextInput
        style={styles.input}
        placeholder="Local onde foi encontrado"
        placeholderTextColor="#8a8a8f"
        value={itemLocation}
        onChangeText={setItemLocation}
      />
      <TextInput
        style={styles.input}
        placeholder="Categoria"
        placeholderTextColor="#8a8a8f"
        value={itemCategory}
        onChangeText={setItemCategory}
      />

      {/* Adicione outros campos de acordo com as informações que você deseja cadastrar */}

      <TouchableOpacity style={styles.createPostButton} onPress={handleSubmit}>
        <MaterialIcons name="post-add" size={25} color="#fff" />
        <Text style={styles.createPostButtonText}>Criar Post</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f4f4f5',
    padding: 20,
  },
  selectedImage: {
    width: 300,
    height: 300,
    borderRadius: 10,
    marginBottom: 20,
  },
  selectImageButton: {
    flexDirection: 'row',
    alignItems: 'center',
    marginBottom: 20,
  },
  selectImageText: {
    fontSize: 18,
    color: '#22c55e',
    marginLeft: 10,
  },
  input: {
    width: '100%',
    height: 50,
    backgroundColor: '#fff',
    borderRadius: 5,
    paddingHorizontal: 10,
    fontSize: 16,
    color: '#27272a',
    marginBottom: 15,
  },
  createPostButton: {
    backgroundColor: '#22c55e',
    width: '100%',
    height: 50,
    borderRadius: 5,
    justifyContent: 'center',
    alignItems: 'center',
    marginTop: 30,
    flexDirection: 'row',
  },
  createPostButtonText: {
    color: '#fff',
    fontSize: 18,
    fontWeight: 'bold',
    marginLeft: 10,
  },
});

export default NewPostScreen;
