//importar stylesheet da biblioteca RN
import { StyleSheet } from 'react-native';

export default StyleSheet.create({
	container: {
		flex: 1,
		backgroundColor: 'yellow',
	},
	logoContainer: {
		flex: 1,
		backgroundColor: 'green',
	},
	inputContainer: {
		flex: 2,
		backgroundColor: 'orange',
	},
	signupContainer: {
		flex: 1,
		backgroundColor: 'red',
	},
});

