import React, { useState } from 'react';
import { View, Text, Image, FlatList, TouchableOpacity, StyleSheet } from 'react-native';
import { createBottomTabNavigator } from '@react-navigation/bottom-tabs';

const Tab = createBottomTabNavigator();

const Post = ({ post, onLikePress }) => {
  const { id, user, objectName, imageUri, likes, isFound, time } = post;
  const [isLiked, setIsLiked] = useState(false);

  const handleLikePress = () => {
    setIsLiked(!isLiked);
    onLikePress(id, !isLiked);
  };

  return (
    <View style={styles.postContainer}>
      {/* Aqui você pode renderizar o conteúdo de cada post */}
      <Text style={styles.objectName}>{objectName}</Text>
      <Image source={{ uri: imageUri }} style={styles.postImage} resizeMode="cover" />
      <TouchableOpacity onPress={handleLikePress}>
        <Text style={styles.likes}>
          {isLiked ? 'Curtido' : 'Curtir'} ({likes})
        </Text>
      </TouchableOpacity>
      <Text style={styles.isFound}>{isFound ? 'Achado' : 'Perdido'}</Text>
      <Text style={styles.time}>{time}</Text>
      {/* Aqui você pode adicionar outros elementos do post, como foto do usuário, comentários, etc. */}
    </View>
  );
};

const Feed = () => {
  const [posts, setPosts] = useState([
    {
      id: '1',
      user: {
        name: 'Nome do Usuário',
        profileImage: 'https://example.com/profile_image.jpg',
      },
      objectName: 'Objeto A',
      imageUri: 'https://example.com/post_image.jpg',
      likes: 100,
      isFound: true,
      time: 'Há 1 hora',
    },
    // Adicione mais posts aqui
  ]);

  const handleLikePress = (postId, isLiked) => {
    const updatedPosts = posts.map((post) => {
      if (post.id === postId) {
        return {
          ...post,
          likes: isLiked ? post.likes - 1 : post.likes + 1,
        };
      }
      return post;
    });
    setPosts(updatedPosts);
  };

  return (
    <View style={styles.container}>
      <FlatList
        data={posts}
        keyExtractor={(item) => item.id}
        renderItem={({ item }) => <Post post={item} onLikePress={handleLikePress} />}
      />

      <Tab.Navigator>
        <Tab.Screen name="Feed" component={Feed} />
        <Tab.Screen name="Procurar" component={SearchScreen} />
        <Tab.Screen name="Novo Post" component={NewPostScreen} />
        <Tab.Screen name="Perfil" component={ProfileScreen} />
        <Tab.Screen name="Configurações" component={ConfigScreen} />
      </Tab.Navigator>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f4f4f5',
  },
  postContainer: {
    backgroundColor: '#fff',
    marginBottom: 10,
    padding: 10,
    borderRadius: 5,
  },
  objectName: {
    fontSize: 18,
    fontWeight: 'bold',
    color: '#27272a',
  },
  postImage: {
    width: '100%',
    height: 300,
    borderRadius: 5,
  },
  likes: {
    fontSize: 14,
    color: '#27272a',
  },
  isFound: {
    fontSize: 16,
    color: '#27272a',
  },
  time: {
    fontSize: 12,
    color: '#27272a',
  },
});

export default Feed;
