import React, { useState } from 'react';
import { View, Text, TouchableOpacity, StyleSheet, Switch } from 'react-native';
import MaterialIcons from 'react-native-vector-icons/MaterialIcons';

const ConfigScreen = () => {
  const [achadosPerdidos, setAchadosPerdidos] = useState(true); // Estado para habilitar/desabilitar a funcionalidade de achados e perdidos
  const [modoEscuro, setModoEscuro] = useState(false); // Estado para habilitar/desabilitar o modo escuro

  // Função para habilitar/desabilitar a funcionalidade de achados e perdidos
  const toggleAchadosPerdidos = () => {
    setAchadosPerdidos(!achadosPerdidos);
  };

  // Função para habilitar/desabilitar o modo escuro
  const toggleModoEscuro = () => {
    setModoEscuro(!modoEscuro);
    // Implemente a lógica para mudar o tema do aplicativo para modo escuro ou claro
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity style={styles.configOption} onPress={toggleAchadosPerdidos}>
        <Text style={styles.optionText}>Habilitar funcionalidade de Achados e Perdidos</Text>
        <Switch value={achadosPerdidos} onValueChange={toggleAchadosPerdidos} />
      </TouchableOpacity>

      {/* Outras opções de configuração relacionadas ao app */}

      <TouchableOpacity style={styles.themeOption} onPress={toggleModoEscuro}>
        <MaterialIcons name={modoEscuro ? 'brightness-4' : 'brightness-7'} size={24} color="#22c55e" />
        <Text style={styles.themeOptionText}>Modo {modoEscuro ? 'Claro' : 'Escuro'}</Text>
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f4f4f5',
    padding: 20,
  },
  configOption: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    alignItems: 'center',
    marginBottom: 20,
  },
  optionText: {
    fontSize: 18,
    color: '#27272a',
  },
  themeOption: {
    flexDirection: 'row',
    alignItems: 'center',
  },
  themeOptionText: {
    marginLeft: 10,
    fontSize: 18,
    color: '#22c55e',
  },
});

export default ConfigScreen;
